mapper.select:
select
    *
from
    pms_attr_group


        attr_group_id = #{dto.attrGroupId}


        and attr_group_name = #{dto.attrGroupName}


        and sort = #{dto.sort}


        and descript = #{dto.descript}


        and icon = #{dto.icon}


        and catelog_id = #{dto.catelogId}

